import React, { useState } from "react";
import styled, { css } from "styled-components";

const NavigationBar = styled.div`
  margin: 0;
  .nav {
    position: fixed;
    left: 10px;
    width: 100%;
    max-width: 1280px;
    margin: 0 auto;
    box-sizing: border-box;
    height: 120px;
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding-left: 40px;
    padding-right: 40px;
    z-index: 3;
    background: #fff;
  }
  .nav span {
    display: inline-block;
    font-size: 24px;
    color: #101010;
    font-weight: bold;
    white-space: nowrap;
  }
  .menu {
    display: flex;
    list-style: none;
    font-size: 20px;
    padding: 0;
    margin: 0;
  }
  .menu li:not(:nth-child(1)) {
    padding-left: 32px;
  }
  .menu li:nth-child(4) {
    opacity: 0.2;
  }
  .menu li a {
    text-decoration: none;
    color: #101010;
  }
  .nav_Toggle {
    position: absolute;
    right: 80px;
    display: none;
  }
  .nav_Toggle span {
    position: absolute;
    transition: 0.3s ease-in-out;
    height: 4px;
    width: 30px;
    border-radius: 7px;
    background-color: #3d4146;
    top: 0;
    transition: 0.3s;
  }
  .nav_Toggle span:nth-child(1) {
    top: -5px;
  }
  .nav_Toggle span:nth-child(2),
  .nav_Toggle span:nth-child(3) {
    top: 5px;
  }
  .nav_Toggle span:nth-child(4) {
    top: 15px;
  }
  @media screen and (max-width: 768px) {
    .nav {
      flex-direction: column;
      align-items: flex-start;
      justify-content: start;
      padding-top: 16px;
      padding-bottom: 16px;
      height: 84px;
      padding-left: 16px;
      padding-right: 16px;
    }
    .menu {
      display: none;
      flex-direction: column;
      width: 100%;
      height: 20%;
      padding-top: 70px;
      justify-content: space-evenly;
    }
    .menu li:nth-child(1) {
      padding-left: 20px;
    }
    .menu li:nth-child(2) {
      padding-left: 20px;
    }
    .menu li:nth-child(3) {
      padding-left: 20px;
    }
    .menu li:nth-child(4) {
      padding-left: 20px;
    }
    .menu li a {
      color: #fff;
    }
    .nav_Toggle {
      display: block;
      top: 16px;
    }
  }
  ${(props) =>
    props.active &&
    css`
      .nav {
        width: 100vw;
        height: 100%;
        background-color: #232323;
      }
      v span {
        color: #fff;
      }
      .menu {
        display: flex;
      }
      .nav_Toggle span {
        background-color: #fff;
      }
      .nav_Toggle span:nth-child(1) {
        opacity: 0;
      }
      .nav_Toggle span:nth-child(2) {
        transform: rotate(45deg);
      }
      .nav_Toggle span:nth-child(3) {
        transform: rotate(-45deg);
      }
      .nav_Toggle span:nth-child(4) {
        opacity: 0;
      }
    `}
`;

const MenuBtn = styled.div``;
function Navbar() {
  const [active, setActive] = useState(false);
  const onToggle = () => {
    console.log(active);
    setActive(!active);
  };
  return (
    <NavigationBar onToggle={onToggle} active={active}>
      <div className="nav">
        <span>Endless Creation</span>
        <ul className="menu">
          <li>
            <a href="#">Activity</a>
          </li>
          <li>
            <a href="#">Project</a>
          </li>
          <li>
            <a href="#">Member</a>
          </li>
          <li>
            <a href="#">community</a>
          </li>
        </ul>
        <a href="#" className="nav_Toggle" onClick={onToggle}>
          <span></span>
          <span></span>
          <span></span>
          <span></span>
        </a>
      </div>
    </NavigationBar>
  );
}

export default Navbar;
