import React from "react";
import styled, { css } from "styled-components";
import { useBtnState } from "../../Context/MainContext";
import LinkItem from "../atoms/LinkItem";
const NavList = styled.li`
  list-style: none;
  a {
    text-decoration: none;
    color: #101010;
  }
  .link {
    text-decoration: none;
  }
  @media screen and (max-width: 1280px) {
    ${(props) =>
      props.active &&
      css`
        a {
          color: #fff;
        }
      `}
  }
`;

const ArrowRightShow = styled.span`
  position: relative;
  width: 15px;
  height: 15px;
  font-size: 15px;
  margin-left: 4px;
  color: #fff;
`;

function MenuList() {
  const active = useBtnState();
  return (
    <>
      <LinkItem to={"/Activity"}>Activity</LinkItem>
      <LinkItem to={"/Activity"}>Project</LinkItem>
      <LinkItem to={"/Activity"}>Member</LinkItem>
      <LinkItem to={"/"}>Community</LinkItem>
      <ArrowRightShow>
        <img src="images/RightArrow.png" />
      </ArrowRightShow>
    </>
  );
}

export default MenuList;
