import React, { useState, createContext } from "react";
import styled, { css } from "styled-components";
import NavbarMain from "../../Components/atoms/NavbarMain";
import NavbarMenu from "../../Components/organisms/NavbarMenu";
import HamburgerBtn from "../../Components/atoms/HamburgerBtn";
/* 상단 고정 네이게이션 바 Template*/
const NavbarTemplate = styled.div`
  position: fixed;
  top: 0px;
  width: 100%;
  height: 120px;
  box-sizing: border-box;
  display: flex;
  justify-content: space-between;
  align-items: center;
  background: #fff;
  padding-left: 70px;
  padding-right: 70px;
  z-index: 3;

  @media screen and (max-width: 1280px) {
    padding-left: 80px;
    padding-right: 70px;
    height: 84px;
  }
  ${(props) =>
    props.active &&
    css`
      height: 100%;
      background-color: #232323;
      justify-content: flex-start;
      align-items: flex-start;
      flex-direction: column;
      padding-top: 24px;
    `}
`;
const BtnActiveProvider = createContext();
function MainNavbar() {
  const [active, setActive] = useState(false);
  const onToggle = () => {
    console.log(active);
    setActive(!active);
  };

  return (
    <BtnActiveProvider value={active}>
      <NavbarTemplate>
        <NavbarMain />
        <NavbarMenu />
        <HamburgerBtn />
      </NavbarTemplate>
    </BtnActiveProvider>
  );
}

export default MainNavbar;
